mapper.select:
select s.id, sum(s.amount) as amount, sum(s.sum) as sum, p.name as name, p.kind FROM ${tableName1} s left join ${tableName2} p on s.id = p.id where DATE_SUB(CURDATE(), INTERVAL ${day} DAY) <= date(time)
and p.kind = #{kind}
group by id;

select count(0) from ${tableName}

	where 
	DATE_SUB(CURDATE(), INTERVAL ${day} DAY) <= date(time)
